document.querySelectorAll(".nav-link").forEach(e=>{new URL(e.href,window.location.origin).pathname===window.location.pathname&&e.classList.add("active")}),document.getElementById("openModalBtn").onclick=function(){document.getElementById("myModal").classList.add("active")},document.querySelectorAll(".closeModalBtn").forEach(function(e){e.addEventListener("click",function(){document.getElementById("myModal").classList.remove("active")})}),document.querySelector(".modal__backdrop").onclick=function(){document.getElementById("myModal").classList.remove("active")};